mapper.select:

select *
	from(
		select rownum as rn, bno, title, writer, content, regdate, updatedate
		from(
			select * 
			from freeboard 









	where title like '%'||#{searchName}||'%'
	where writer like '%'||#{searchName}||'%'
	where content like '%'||#{searchName}||'%'
	where titcont like '%'||#{searchName}||'%' or title
		like '%'||#{searchName}||'%'>

	 where 1=1 


			order by bno desc
		)
	)
where rn > (#{pageNum}-1) * #{amount}  and rn <= #{pageNum} * #{amount}
